const results = [];

for (const item of $input.all()) {
  const data = item.json || {};

  // 1) get keywords (handle trailing spaces in keys)
  const keywords = (data["Search Keywords "] || data["Search Keywords"] || data["Search Keywords"]?.toString() || '').toString().trim();

  // 2) find possible sort value by scanning keys first (keys that include 'sort' or 'order')
  let sortRaw = '';
  // preferred explicit keys to check in order
  const preferKeys = ["Sort By ", "Sort By", "SortBy", "sort_by", "sort", "Order", "Order By", "order_by"];
  for (const k of preferKeys) {
    if (Object.prototype.hasOwnProperty.call(data, k) && data[k] != null && ('' + data[k]).toString().trim() !== '') {
      sortRaw = ('' + data[k]).toString().trim();
      break;
    }
  }

  // if not found, fallback to scanning any key name that contains 'sort' or 'order'
  if (!sortRaw) {
    for (const k of Object.keys(data)) {
      if (k.toLowerCase().includes('sort') || k.toLowerCase().includes('order')) {
        const val = ('' + data[k]).toString().trim();
        if (val) {
          sortRaw = val;
          break;
        }
      }
    }
  }

  // 3) if still not found, check values of other fields just in case someone typed the word inside another input
  if (!sortRaw) {
    for (const k of Object.keys(data)) {
      const val = ('' + data[k]).toString().trim();
      if (/^(latest|recent|top match|top-match|relevance|best|date_posted|date posted|date)$/i.test(val)) {
        sortRaw = val;
        break;
      }
    }
  }

  // Normalize sort value
  const sortNormalized = (sortRaw || 'latest').toString().trim().toLowerCase();

  // Map via regex to cover many variants
  let mappedSort;
  if (/latest|recent|date|date_posted|date posted/i.test(sortNormalized)) {
    mappedSort = 'date_posted';
  } else if (/top|match|relev|best|priority/i.test(sortNormalized)) {
    mappedSort = 'relevance';
  } else {
    // final fallback
    mappedSort = 'date_posted';
  }

  // 4) datePosted mapping (robust)
  const datePostedRaw = (data["Date Posted Filter "] || data["Date Posted"] || data["Date Posted Filter"] || data["date_posted"] || '').toString().trim().toLowerCase();
  const dp = datePostedRaw;
  let mappedDate;
  if (/24/.test(dp) || /past.*24/.test(dp) || /past.*24h/.test(dp) || /24h/.test(dp) || /24 hours/.test(dp)) {
    mappedDate = 'past-24h';
  } else if (/week/.test(dp)) {
    mappedDate = 'past-week';
  } else if (/month/.test(dp) || dp === '') {
    mappedDate = 'past-month';
  } else {
    mappedDate = 'past-month';
  }

  // Build URL
  const baseUrl = 'https://www.linkedin.com/search/results/content/';
  const q = [];

  if (keywords) q.push('keywords=' + encodeURIComponent(keywords));
  q.push('datePosted=' + encodeURIComponent(mappedDate));
  q.push('sortBy=' + encodeURIComponent(mappedSort));
  q.push('origin=FACETED_SEARCH');

  const constructedUrl = baseUrl + '?' + q.join('&');

  results.push({
    json: { constructedUrl }
  });
}

return results;

