const results = [];

for (const item of $input.all()) {
  const rawText = item.json.text;

  try {
    const parsed = JSON.parse(rawText);

    results.push({
      json: {
        message_1: parsed.message_1,
        message_2: parsed.message_2,
        message_3: parsed.message_3,
      }
    });

  } catch (err) {
    // In case JSON parsing fails, return the raw text with an error message
    results.push({
      json: {
        error: "Failed to parse message JSON",
        originalText: rawText
      }
    });
  }
}

return results;
