// Process LinkedIn Jobs Data - Extract Unique Companies
const companiesMap = new Map();

function cleanLinkedInUrl(rawUrl) {
  if (!rawUrl) return '';
  let u = rawUrl.trim();

  // Ensure protocol exists for URL parsing
  if (!/^https?:\/\//i.test(u)) u = 'https://' + u;

  try {
    const urlObj = new URL(u);

    // Build canonical LinkedIn company/school URL
    if (urlObj.hostname.toLowerCase().includes('linkedin.com')) {
      const m = urlObj.pathname.match(/^\/(company|school)\/([^\/]+)/i);
      if (m) {
        return `https://www.linkedin.com/${m[1]}/${m[2]}`;
      }
    }

    // Fallback: origin + path (no trailing slash)
    return `${urlObj.origin}${urlObj.pathname.replace(/\/+$/, '')}`;

  } catch (e) {
    // Fallback: strip query/fragment and trailing slashes
    const stripped = u.split(/[?#]/)[0].replace(/\/+$/, '');
    return /^https?:\/\//i.test(stripped) ? stripped : `https://${stripped}`;
  }
}

function cleanDomain(rawUrl) {
  if (!rawUrl) return '';
  let u = rawUrl.trim();

  // Ensure protocol exists for URL parsing
  if (!/^https?:\/\//i.test(u)) u = 'https://' + u;

  try {
    const urlObj = new URL(u);
    return urlObj.hostname.replace(/^www\./i, ''); // strip "www."
  } catch (e) {
    // fallback: regex
    const domainMatch = u.match(/^(?:https?:\/\/)?(?:www\.)?([^\/?#]+)/i);
    return domainMatch ? domainMatch[1].toLowerCase() : '';
  }
}

// Loop over all input items (job listings)
for (const item of $('Get LinkedIn Job Posts by Job Title1').all()) {
  const job = item.json;

  if (job.companyName && job.companyWebsite) {
    const companyKey = job.companyName.trim().toLowerCase();

    // Extract clean domain
    const domain = cleanDomain(job.companyWebsite);

    // Clean LinkedIn URL
    const rawLinkedin =
      job.companyLinkedinUrl ||
      job.companyLinkedInUrl ||
      job.linkedin_url ||
      job.linkedinUrl ||
      '';
    const linkedinUrl = cleanLinkedInUrl(rawLinkedin);

    // Only store if company doesn't exist yet (keep first occurrence) and domain exists
    if (!companiesMap.has(companyKey) && domain) {
      companiesMap.set(companyKey, {
        name: job.companyName.trim(),
        domain: domain, // ✅ always just "example.com"
        website: job.companyWebsite,
        linkedin_url: linkedinUrl,
        industry: job.industries || '',
        employee_count: job.companyEmployeesCount || 0,
        location: job.companyAddress ?
          `${job.companyAddress.addressLocality || ''}, ${job.companyAddress.addressRegion || ''}`
            .replace(', ,', '')
            .replace(/^,|,$/, '') : '',
        date_discovered: new Date().toISOString().split('T')[0]
      });
    }
  }
}

// Convert to array and return
const uniqueCompanies = Array.from(companiesMap.values());
return uniqueCompanies.map(company => ({ json: company }));

