/**
 * Constructs Apollo.io "people/match" API URLs dynamically from input data.
 * Each input item represents one person record.
 */

const baseUrl = "https://api.apollo.io/api/v1/people/match";

// Get all incoming items
const items = $input.all();

return items.map(item => {
  const data = item.json;

  // Extract and encode parameters safely
  const name = encodeURIComponent(data["Name"] || "");
  const domain = encodeURIComponent(data["Company Domain"] || "");
  const linkedinUrl = encodeURIComponent(data["LinkedIn Profile URL"] || "");

  // Build the Apollo API URL
  const apolloUrl = `${baseUrl}?name=${name}&domain=${domain}&linkedin_url=${linkedinUrl}&reveal_personal_emails=false&reveal_phone_number=false`;

  return {
    json: {
      ...data,
      apollo_match_url: apolloUrl
    }
  };
});

