const a = Date.now().toString(36);
const b = Math.random().toString(36).substring(2, 10);
return [{ json: { sessionId: `${a}-${b}` } }];

const newItems = [];

for (const item of $input.all()) {
  try {
    const parsed = JSON.parse(item.json.text);

    newItems.push({
      json: {
        overallCompletionRate: parsed.overallCompletionRate,
        keyWin: parsed.keyWin,
        criticalIssue: parsed.criticalIssue,
        actionRequired: parsed.actionRequired,
        resourceNeeds: parsed.resourceNeeds || '',
        decisionsNeeded: parsed.decisionsNeeded || '',
      }
    });
  } catch (error) {
    newItems.push({
      json: {
        error: `JSON parsing failed: ${error.message}`
      }
    });
  }
}

return newItems;

const items = $input.all();

// Extract raw message text and remove bot mention
let text = items[0]?.json?.text || "";
text = text.replace(/<@[\w]+>\s*/, "").trim().toLowerCase();

// Create sessionId
const sessionId = `${items[0]?.json?.user}-${items[0]?.json?.channel}`;

// Determine if this is a reset command
const resetMemory = text === '/reset';

return [{ 
  json: { 
    sessionId, 
    originalMessage: text, 
    resetMemory 
  } 
}];

