={{ $json.output
   // Headings: #, ##, ###... ‚Üí *Heading*
   .replace(/^#{1,6} (.*)$/gm, "*$1*")

   // Bold: **bold** ‚Üí *bold*
   .replace(/\*\*(.*?)\*\*/g, "*$1*")

   // Italics: *italic* or _italic_ ‚Üí _italic_
   .replace(/(^|[^*])\*(?!\s)([^*]+?)\*(?!\s)/g, "$1_$2_")  // single * ‚Üí _
   .replace(/_(.*?)_/g, "_$1_")

   // Inline code: `code` ‚Üí `code`
   .replace(/`([^`]+)`/g, "`$1`")

   // Code block: ```...``` ‚Üí ```...```
   .replace(/```([\s\S]*?)```/g, "```\n$1\n```")

   // Links: [text](url) ‚Üí <url|text>
   .replace(/\[([^\]]+)\]\(([^)]+)\)/g, "<$2|$1>")

   // Bullets: - item or * item ‚Üí ‚Ä¢ item
   .replace(/^- (.*)$/gm, "‚Ä¢ $1")
   .replace(/^\* (.*)$/gm, "‚Ä¢ $1")

   // Convert escaped \n to actual newlines
   .replace(/\\n/g, "\n")
}}

