const a = Date.now().toString(36);
const b = Math.random().toString(36).substring(2, 10);
return [{ json: { sessionId: `${a}-${b}` } }];

const results = [];

// Helper: normalize strings/arrays into semicolon-joined string
function asJoined(val) {
  if (Array.isArray(val)) return val.join("; ");
  if (typeof val === "string") return val;
  return null;
}

for (const item of $input.all()) {
  let rawOutput = item.json.output;
  let parsed;

  try {
    // Clean fenced JSON if wrapped in ```json ... ```
    rawOutput = rawOutput.replace(/```json|```/g, '').trim();
    parsed = JSON.parse(rawOutput);
  } catch (err) {
    throw new Error("‚ùå Failed to parse orchestrator JSON: " + err.message);
  }

  // Base row
  const row = {
    // === Market Size & Growth ===
    market_overview: parsed.market_size_and_growth?.market_overview ?? null,
    current_size_value: parsed.market_size_and_growth?.current_size?.value ?? null,
    current_size_currency: parsed.market_size_and_growth?.current_size?.currency ?? null,
    current_size_year: parsed.market_size_and_growth?.current_size?.year ?? null,
    current_size_source: parsed.market_size_and_growth?.current_size?.source ?? null,
    forecasted_size_value: parsed.market_size_and_growth?.forecasted_size?.value ?? null,
    forecasted_size_currency: parsed.market_size_and_growth?.forecasted_size?.currency ?? null,
    forecasted_size_year: parsed.market_size_and_growth?.forecasted_size?.year ?? null,
    forecasted_size_cagr: parsed.market_size_and_growth?.forecasted_size?.cagr ?? null,
    forecasted_size_source: parsed.market_size_and_growth?.forecasted_size?.source ?? null,
    key_drivers: asJoined(parsed.market_size_and_growth?.key_drivers),
    challenges: asJoined(parsed.market_size_and_growth?.challenges),
    regional_trends: asJoined(parsed.market_size_and_growth?.regional_trends),
    market_size_sources: asJoined(parsed.market_size_and_growth?.sources),

    // === Competitive Analysis ===
    competitive_overview: parsed.competitive_analysis?.overview ?? null,
    competitive_sources: asJoined(parsed.competitive_analysis?.sources),

    // === Markdown Outputs ===
    market_size_and_growth_markdown: parsed.markdown_outputs?.market_size_and_growth ?? null,
    competitive_analysis_markdown: parsed.markdown_outputs?.competitive_analysis ?? null
  };

  // === Competitors (expand into fixed 5 slots) ===
  const competitors = parsed.competitive_analysis?.competitors || [];
  for (let i = 1; i <= 5; i++) {
    const comp = competitors[i - 1] || {};
    row[`competitor${i}_name`] = comp.name ?? null;
    row[`competitor${i}_overview`] = comp.overview ?? null;
    row[`competitor${i}_products_services`] = asJoined(comp.products_services);
    row[`competitor${i}_revenue_or_funding`] = comp.revenue_or_funding ?? null;
    row[`competitor${i}_market_share`] = comp.market_share ?? null;
    row[`competitor${i}_reason_for_inclusion`] = comp.reason_for_inclusion ?? null;
    row[`competitor${i}_strengths`] = asJoined(comp.strengths);
    row[`competitor${i}_weaknesses`] = asJoined(comp.weaknesses);
    row[`competitor${i}_opportunities`] = asJoined(comp.opportunities);
    row[`competitor${i}_threats`] = asJoined(comp.threats);
    row[`competitor${i}_sources`] = asJoined(comp.sources);

    // === New Business Process / Value Fields ===
    row[`competitor${i}_business_model`] = comp.business_model ?? null;
    row[`competitor${i}_value_proposition`] = comp.value_proposition ?? null;
    row[`competitor${i}_key_partners`] = asJoined(comp.key_partners);
    row[`competitor${i}_key_resources`] = asJoined(comp.key_resources);
    row[`competitor${i}_key_activities`] = asJoined(comp.key_activities);
    row[`competitor${i}_customer_segments`] = asJoined(comp.customer_segments);
    row[`competitor${i}_channels`] = asJoined(comp.channels);
    row[`competitor${i}_customer_relationships`] = asJoined(comp.customer_relationships);
    row[`competitor${i}_competitive_advantage`] = comp.competitive_advantage ?? null;
  }

  results.push({ json: row });
}

return results;

