const results = [];

for (const item of $input.all()) {
  let rawText = item.json.text;
  let parsed = null;

  // Clean fenced JSON
  rawText = rawText.replace(/```json|```/g, '').trim();

  // Remove markdown links: [label](url) ‚Üí label
  rawText = rawText.replace(/\[([^\]]+)\]\([^)]+\)/g, '$1');

  try {
    parsed = JSON.parse(rawText);
  } catch (err) {
    // Fallback: regex extraction (if JSON is malformed)
    const matchProblem = rawText.match(/"problem"\s*:\s*"([\s\S]*?)",/);
    const matchSolution = rawText.match(/"solution"\s*:\s*"([\s\S]*?)",/);
    const matchOpportunity = rawText.match(/"opportunity"\s*:\s*"([\s\S]*?)"/);

    parsed = {
      problem: matchProblem ? matchProblem[1] : null,
      solution: matchSolution ? matchSolution[1] : null,
      opportunity: matchOpportunity ? matchOpportunity[1] : null,
    };
  }

  const row = {
    problem: parsed.problem ?? null,
    solution: parsed.solution ?? null,
    opportunity: parsed.opportunity ?? null,
  };

  results.push({ json: row });
}

return results;

