const items = $input.all();

const headers = [
  "Year",
  "Quarter",
  "Projected Revenue (USD)",
  "Projected Expenses (USD)",
  "Cash Flow (USD)",
  "Customer Growth",
  "MRR (USD)",
  "Cost of Goods Sold (USD)",
  "Operating Expenses (USD)",
  "Total Costs (USD)",
  "Gross Profit (USD)",
  "Net Income (USD)"
];

let csvOutput = headers.join(";") + "\n";

for (const item of items) {
  const row = headers.map(h => item.json[h] ?? "").join(";");
  csvOutput += row + "\n";
}

return [
  {
    json: {
      dataset: csvOutput.trim()
    }
  }
];

const results = [];

for (const item of $input.all()) {
  const chart = item.json.output.chart; // your Chart.js object

  const payload = {
    chart: chart,           // the Chart.js object
    width: 800,             // canvas width in pixels
    height: 450,            // canvas height in pixels
    format: "png",          // image format
    devicePixelRatio: 2     // for high-res images
  };

  results.push({
    json: {
      chartPayload: payload,
      slug: item.json.output.slug
    }
  });
}

return results;

